	public void drawDT(boolean showMST, boolean alphaShape, boolean convexHull,
			double[] d1, double[] d2, List MST, double area, double peri, Triangulation DT,
			float xoff, float yoff_, int transform) {
		this.noStroke();
		float size = 395;
		float margin = 20;
		float gap = 100;
		float pSize1 = 9;
		float pSize = 6;
		float sat =221;
		float xLeft =22;
		
		
		float yoff =y+ yoff_;
		
// Draw Input data **********************************************
		this.textFont(metaBold, 30);
		fill(0,0,0);
		this.textAlign(PApplet.CENTER);
		String text = "Input data";
		if (transform==1)
			text = "Sqrt";
		if (transform==2)
			text = "Log";
		//this.text(text,(float) (xoff+size/2),yoff-30);
		
		
		// MST ********************************************************************
				this.strokeWeight(1);
				fill(sat,sat,sat);
				stroke(0,0,0);
				rect(xoff-margin,yoff-margin, (float) ((size+margin*2)), (float) ((size+margin*2)));
				noFill();
				stroke(0,0,0);
				rect(xoff-margin,yoff-margin, (float) ((size+margin*2)), (float) ((size+margin*2)));
				
				this.strokeWeight(1);
				if (showMST){
					this.stroke(0,0,0,120);
					for (int i = 0; i < MST.size(); i++) {
					    Edge e = (Edge) MST.get(i);
					    float x1 = (float) (xoff + e.p1.x*size/(1000));
						float x2 = (float) (xoff + e.p2.x*size/(1000));
						float y1 = (float) (yoff + e.p1.y*size/(1000));
						float y2 = (float) (yoff + e.p2.y*size/(1000));
						this.line(x1, y1, x2, y2);
					}
				}
				noStroke();
				this.fill(Color.BLACK.getRGB());
				for (int i = 0; i < data1.length; i++) {
					float xx = (float) (xoff + d1[i]*size);
					float yy = (float) (yoff + d2[i]*size);
					this.ellipse(xx, yy, pSize, pSize);
				}
				
				this.textAlign(PApplet.RIGHT);
				// Draw Text MST
				fill(0,0,0);
				float cX = xoff + size/2;
				float cY = yoff-30;
				this.textAlign(PApplet.CENTER);
				this.text("MST",cX,cY);
		
		
// Alpha Shape **********************************************		
		xoff +=(size+gap);
		this.strokeWeight(1);
		fill(sat,sat,sat);
		stroke(0,0,0);
		rect(xoff-margin,yoff-margin, (float) ((size+margin*2)), (float) ((size+margin*2)));
		this.noFill();
		stroke(0,0,0);
		rect(xoff-margin,yoff-margin, (float) ((size+margin*2)), (float) ((size+margin*2)));
		
		if (alphaShape){
		  // DRAW triangle forming ALPHA SHAPE
			this.stroke(255,255,255);
			this.fill(0,0,0,60);
			Iterator tri = DT.triangles.iterator();
		    this.curveTightness(1); 
			int count=0;
		    while (tri.hasNext()) {
				Triangle triangle = (Triangle) tri.next();
				if (triangle.onComplex) {
			        Edge e1 = triangle.anEdge;
	                Edge e2 = triangle.anEdge.nextE;
	                Edge e3 = triangle.anEdge.nextE.nextE;
	                float x1 = (float) (xoff + (e1.p1.x)*size/(1000));
	                float x2 = (float) (xoff + (e2.p1.x)*size/(1000));
	                float x3 = (float) (xoff + (e3.p1.x)*size/(1000));
	                float y1 = (float) (yoff + (e1.p1.y)*size/(1000));
	                float y2 = (float) (yoff + (e2.p1.y)*size/(1000));
	                float y3 = (float) (yoff + (e3.p1.y)*size/(1000));
	                
	                this.beginShape();
	                this.curveVertex(x1, y1);
	                this.curveVertex(x1, y1);
	                this.curveVertex(x2, y2);
	                this.curveVertex(x3, y3);
	                this.curveVertex(x1, y1); 
	                this.curveVertex(x1, y1); 
	                this.endShape();
	                count++;
				 }
	        }
		 //   System.out.println("count: "+count);
		}
		// Draw Text Alpha Shape
		fill(0,0,0);
		  cX = xoff+ size/2+gap;
		   cY = yoff -30;
		 this.textAlign(PApplet.RIGHT);
		 this.text("Alpha Shape",cX,cY);
		
			
	      //DRAW ALPHA SHAPE
			this.stroke(255,255,255);
			Iterator it = DT.edges.iterator();
		    while (it.hasNext()) {
	            Edge e = (Edge) it.next();
	            if (e.onShape) {
	            	float x1 = (float) (xoff + (e.p1.x)*size/(1000));
	    			float x2 = (float) (xoff + (e.p2.x)*size/(1000));
	    			float y1 = (float) (yoff + (e.p1.y)*size/(1000));
	    			float y2 = (float) (yoff + (e.p2.y)*size/(1000));
	    			this.line(x1, y1, x2, y2);
	            }
		      }
		      
		    //p.fill(Color.CYAN.getRGB());
		    //p.text((int)areaAlpha[fileID]+"/"+(int)periAlpha[fileID],xoff,yoff+16);
			noStroke();
			this.fill(Color.BLACK.getRGB());
			for (int i = 0; i < data1.length; i++) {
				float xx = (float) (xoff + d1[i]*size);
				float yy = (float) (yoff + d2[i]*size);
				this.ellipse(xx, yy, pSize, pSize);
			}
				
	   
		
// DRAW HULL ********************************************************************
		xoff = xoff+(size+gap);
		this.strokeWeight(1);
		fill(sat,sat,sat);
		stroke(0,0,0);
		rect(xoff-margin,yoff-margin, (float) ((size+margin*2)), (float) ((size+margin*2)));
		if (convexHull){
			this.stroke(255,255,255);
			this.fill(0,0,0,60);
			Edge e = DT.hullStart;
			if (e !=null){
   			 this.beginShape();
   			 int count =0;
   			 do {	float x1 = (float) (xoff + (e.p1.x)*size/(1000));
	    			float x2 = (float) (xoff + (e.p2.x)*size/(1000));
	    			float y1 = (float) (yoff + (e.p1.y)*size/(1000));
	    			float y2 = (float) (yoff + (e.p2.y)*size/(1000));
	    			this.line(x1, y1, x2, y2);
	    			if (count==0)
	    				 this.curveVertex(x1, y1);
			        this.curveVertex(x1, y1);
		            this.curveVertex(x2, y2);
		            e = e.nextH;
		            count++;
		        } while (!e.isEqual(DT.hullStart));
			}
	           this.endShape();
			     
		} 
		noStroke();
		this.fill(Color.BLACK.getRGB());
		for (int i = 0; i < data1.length; i++) {
			float xx = (float) (xoff + d1[i]*size);
			float yy = (float) (yoff + d2[i]*size);
			this.ellipse(xx, yy, pSize, pSize);
		}
		
		
		
		// Draw Text Hull
		fill(0,0,0);
		cX = xoff+ size/2+gap;
		cY = yoff- 30;
		this.text("Convex Hull",cX,cY);
		
		
		
		
	}	

	public void mouseWheel(MouseWheelEvent e) {
		int delta = e.getWheelRotation();
		if (this.keyPressed){
			x -=delta;
		}
		else 
			y -=delta;
	}	
